/**
 * Application title settings.
 */
export interface Title {
  /**
   * Determines whether the title is prefixed or not.
   */
  prefix?: boolean;
  /**
   * Title text.
   */
  text?: string;
  /**
   * Title separator.
   */
  separator?: string;
}
